// Import the logo (currently not used in this component)
import logo from "../Images/logo-t.png";

// Import child components
import Posts from "./Posts";         // Component that lists all posts
import SharePosts from "./SharePost"; // Component to create a new post
import User from "./User";           // Component to display user info

// Import layout components from Reactstrap
import { Container, Row, Col } from "reactstrap";

// Import Redux hook to access state
import { useSelector } from "react-redux";

// Import React Router hook for navigation
import { useNavigate } from "react-router-dom";

// Import useEffect to run side effects
import { useEffect } from "react";

const Home = () => {
  // Create a navigate function to redirect users
  const navigate = useNavigate();

  // Access the current user from Redux store
  const user = useSelector((state) => state.users.user);

  // useEffect runs after component mounts or when 'user' changes
  useEffect(() => {
    // If user is not logged in, redirect to the login page
    if (!user) {
      navigate("/login");
    }
  }, [user]); // dependency array: triggers effect whenever 'user' changes

  return (
    <>
      {/* First Row: User info + Share Post */}
      <Row>
        <Col md={3}>
          {/* Left column: User information */}
          <User />
        </Col>
        <Col md={9}>
          {/* Right column: Component to create/share a post */}
          <SharePosts />
        </Col>
      </Row>

      {/* Second Row: Posts list */}
      <Row>
        <Col md={3}></Col> {/* Empty left column for spacing */}
        <Col md={9}>
          {/* Right column: Display posts */}
          <Posts />
        </Col>
      </Row>
    </>
  );
};

// Export Home component so it can be used in routes or other components
export default Home;
